import Taro from "@tarojs/taro-h5";
import Nerv from "nervjs";
import { View, Text, ScrollView } from '@tarojs/components';
export default class ClScreenDrawer extends Taro.Component {
  render() {
    const props = this.props;

    const hideModal = () => {
      props.onHide && props.onHide();
    };
    return <View>
      <ScrollView scrollY className={`DrawerPage ${props.show ? 'show' : ''}`}>
        {props.renderPage}
      </ScrollView>
      <View className={`DrawerClose ${props.show ? 'show' : ''}`} onClick={hideModal}>
        <Text className="cuIcon-pullright" />
      </View>
      <ScrollView scrollY className={`DrawerWindow ${props.show ? 'show' : ''}`}>
        {props.renderDrawer}
      </ScrollView>
    </View>;
  }

}
ClScreenDrawer.options = {
  addGlobalClass: true
};